import React, { useState } from 'react';
import API from '../api/api';
import { useNavigate } from 'react-router-dom';

export default function Register(){
  const [name,setName]=useState('');
  const [email,setEmail]=useState('');
  const [password,setPassword]=useState('');
  const navigate=useNavigate();

  const submit = async (e) => {
    e.preventDefault();
    try {
      const res = await API.post('/auth/register', { name, email, password });
      localStorage.setItem('ss_token', res.data.token);
      localStorage.setItem('ss_user', JSON.stringify(res.data.user));
      navigate('/');
    } catch (err) {
      alert('Error registering: ' + (err.response?.data?.msg || err.message));
    }
  };

  return (
    <form onSubmit={submit} className="card" style={{maxWidth:420}}>
      <h2>Register</h2>
      <input className="form-control" value={name} onChange={e=>setName(e.target.value)} placeholder="Full name" required />
      <input className="form-control" value={email} onChange={e=>setEmail(e.target.value)} placeholder="Email" required />
      <input className="form-control" value={password} onChange={e=>setPassword(e.target.value)} placeholder="Password" type="password" required />
      <div style={{marginTop:8}}><button className="button" type="submit">Register</button></div>
    </form>
  );
}
