import React, { useState } from 'react';
import API from '../api/api';
import { useNavigate } from 'react-router-dom';

export default function CreateRequest(){
  const [title,setTitle]=useState('');
  const [location,setLocation]=useState('');
  const [description,setDescription]=useState('');
  const [phone,setPhone]=useState('');
  const [files,setFiles]=useState([]);
  const navigate = useNavigate();

  const onSubmit = async (e) => {
    e.preventDefault();
    const form = new FormData();
    form.append('title', title);
    form.append('location', location);
    form.append('description', description);
    form.append('contactPhone', phone);
    for (let f of files) form.append('images', f);
    try {
      await API.post('/requests', form, { headers: { 'Content-Type': 'multipart/form-data' }});
      navigate('/requests');
    } catch (err) {
      console.error(err);
      alert('Error creating request');
    }
  };

  return (
    <form onSubmit={onSubmit} className="card" style={{maxWidth:680}}>
      <h2>Report Rescue</h2>
      <input className="form-control" value={title} onChange={e=>setTitle(e.target.value)} placeholder="Title" required />
      <input className="form-control" value={location} onChange={e=>setLocation(e.target.value)} placeholder="Location" />
      <textarea className="form-control" value={description} onChange={e=>setDescription(e.target.value)} placeholder="Description" />
      <input className="form-control" value={phone} onChange={e=>setPhone(e.target.value)} placeholder="Contact phone" />
      <input type="file" multiple onChange={e=>setFiles(Array.from(e.target.files))} />
      <div style={{marginTop:8}}>
        <button className="button" type="submit">Submit</button>
      </div>
    </form>
  );
}
