import React, { useState } from 'react';
import API from '../api/api';
import { useNavigate } from 'react-router-dom';

export default function Login(){
  const [email,setEmail]=useState('');
  const [password,setPassword]=useState('');
  const navigate=useNavigate();

  const submit = async (e) => {
    e.preventDefault();
    try {
      const res = await API.post('/auth/login', { email, password });
      localStorage.setItem('ss_token', res.data.token);
      localStorage.setItem('ss_user', JSON.stringify(res.data.user));
      navigate('/');
    } catch (err) {
      alert('Invalid credentials');
    }
  };

  return (
    <form onSubmit={submit} className="card" style={{maxWidth:420}}>
      <h2>Login</h2>
      <input className="form-control" value={email} onChange={e=>setEmail(e.target.value)} placeholder="Email" required />
      <input className="form-control" type="password" value={password} onChange={e=>setPassword(e.target.value)} placeholder="Password" required />
      <div style={{marginTop:8}}><button className="button" type="submit">Login</button></div>
    </form>
  );
}
