import React, { useEffect, useState } from 'react';
import API from '../api/api';
import RequestCard from '../components/RequestCard';
import { Link } from 'react-router-dom';

export default function Home(){
  const [requests, setRequests] = useState([]);

  useEffect(()=> {
    API.get('/requests')
      .then(res => setRequests(res.data))
      .catch(err => console.error(err));
  }, []);

  return (
    <div>
      <div style={{display:'flex',justifyContent:'space-between',alignItems:'center',marginBottom:16}}>
        <div>
          <h1 style={{margin:0}}>Serpent Savers</h1>
          <p style={{margin:0,color:'#6b7280'}}>Report snake sightings and rescue calls.</p>
        </div>
        <div>
          <Link className="button" to="/requests/new">Report a Rescue</Link>
        </div>
      </div>

      <section>
        <h2>Recent Requests</h2>
        <div className="request-grid" style={{marginTop:12}}>
          {requests.map(r => <RequestCard key={r._id} r={r} />)}
        </div>
      </section>
    </div>
  );
}
