import React, { useState } from 'react';
import API from '../api/api';

export default function Contact(){
  const [name,setName]=useState('');
  const [email,setEmail]=useState('');
  const [message,setMessage]=useState('');
  const submit = async (e) => {
    e.preventDefault();
    try {
      await API.post('/contact', { name, email, message });
      alert('Message sent');
      setName(''); setEmail(''); setMessage('');
    } catch (err) { alert('Failed to send'); }
  };
  return (
    <form onSubmit={submit} className="card" style={{maxWidth:680}}>
      <h2>Contact Us</h2>
      <input className="form-control" value={name} onChange={e=>setName(e.target.value)} placeholder="Your name" required />
      <input className="form-control" value={email} onChange={e=>setEmail(e.target.value)} placeholder="Your email" required />
      <textarea className="form-control" value={message} onChange={e=>setMessage(e.target.value)} placeholder="Message" required />
      <div style={{marginTop:8}}><button className="button" type="submit">Send</button></div>
    </form>
  );
}
