import React, { useState, useEffect, useRef } from "react";
import "./ImageSlider.css";
import img1 from "../assets/image1.jpg";
import img2 from "../assets/image2.jpg";
import img3 from "../assets/image3.jpg";
import img4 from "../assets/image4.jpg";
import img5 from "../assets/image5.jpg";

const images = [img1, img2, img3, img4, img5];

function ImageSlider() {
  const [current, setCurrent] = useState(0);
  const [fade, setFade] = useState(true);
  const [paused, setPaused] = useState(false);
  const timeoutRef = useRef(null);

  const nextSlide = () => {
    setFade(false);
    setTimeout(() => {
      setCurrent((prev) => (prev + 1) % images.length);
      setFade(true);
    }, 200); // match CSS fade out time
  };
  const prevSlide = () => {
    setFade(false);
    setTimeout(() => {
      setCurrent((prev) => (prev - 1 + images.length) % images.length);
      setFade(true);
    }, 200);
  };

  // Touch (swipe)
  let startX = 0;
  const onTouchStart = e => { startX = e.touches[0].clientX; }
  const onTouchEnd = e => {
    const endX = e.changedTouches[0].clientX;
    if (startX - endX > 50) nextSlide();
    else if (endX - startX > 50) prevSlide();
  }

  // Autoplay, reset fade
  useEffect(() => {
    if (!paused) {
      timeoutRef.current = setTimeout(() => {
        nextSlide();
      }, 3200);
    }
    return () => clearTimeout(timeoutRef.current);
    // eslint-disable-next-line
  }, [current, paused]);

  useEffect(() => {
    return () => clearTimeout(timeoutRef.current);
  }, []);

  return (
    <div
      className="slider-container"
      onMouseEnter={() => setPaused(true)}
      onMouseLeave={() => setPaused(false)}
      onTouchStart={() => setPaused(true)}
      onTouchEnd={() => setPaused(false)}
    >
      <button className="slider-arrow left" onClick={prevSlide}>&lt;</button>
      <div
        className="slider-image-wrap"
        onTouchStart={onTouchStart}
        onTouchEnd={onTouchEnd}
      >
        <img
          src={images[current]}
          className={`slider-image ${fade ? "slider-fade-in" : "slider-fade-out"}`}
          alt={`slide ${current + 1}`}
        />
      </div>
      <button className="slider-arrow right" onClick={nextSlide}>&gt;</button>
      <div className="slider-dots">
        {images.map((_, idx) => (
          <span
            key={idx}
            className={idx === current ? "slider-dot active" : "slider-dot"}
            onClick={() => {
              setFade(false);
              setTimeout(() => {
                setCurrent(idx);
                setFade(true);
              }, 200);
            }}
          />
        ))}
      </div>
    </div>
  );
}

export default ImageSlider;
