import React, { useEffect, useState } from 'react';
import API from '../api/api';

export default function Dashboard(){
  const [requests, setRequests] = useState([]);
  const [users, setUsers] = useState([]);
  const user = JSON.parse(localStorage.getItem('ss_user') || 'null');

  useEffect(()=> {
    // if admin, call admin endpoints
    if (user?.role === 'admin') {
      API.get('/admin/requests').then(r => setRequests(r.data)).catch(console.error);
      API.get('/admin/users').then(r => setUsers(r.data)).catch(console.error);
    } else {
      API.get('/requests').then(r => setRequests(r.data)).catch(console.error);
    }
  }, [user]);

  const promote = async (id) => {
    await API.patch(`/admin/users/${id}/promote`);
    alert('Promoted');
  };

  const updateStatus = async (id, status) => {
    await API.patch(`/requests/${id}/status`, { status });
    alert('Status updated');
    window.location.reload();
  };

  return (
    <div>
      <h2>Dashboard</h2>
      {user?.role === 'admin' && (
        <>
          <h3>Users</h3>
          <div className="card">{users.map(u => <div key={u._id} style={{display:'flex',justifyContent:'space-between'}}><div>{u.name} ({u.email}) - {u.role}</div><div>{u.role!=='admin' && <button onClick={()=>promote(u._id)}>Promote</button>}</div></div>)}</div>
        </>
      )}

      <h3 style={{marginTop:12}}>Requests</h3>
      <div className="request-grid">
        {requests.map(r => (
          <div key={r._id} className="card">
            <h4 style={{margin:0}}>{r.title}</h4>
            <p style={{color:'#6b7280'}}>{r.location}</p>
            <p>{r.description}</p>
            <div style={{marginTop:8}}>
              <button onClick={()=>updateStatus(r._id,'in-progress')}>Mark In-Progress</button>
              <button onClick={()=>updateStatus(r._id,'closed')} style={{marginLeft:8}}>Close</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
