import React, { useEffect, useState, useRef } from "react";
import "./App.css";
import img1 from "../assets/image1.jpg";
import img2 from "../assets/image2.jpg";
import img3 from "../assets/image3.jpg";
import img4 from "../assets/image4.jpg";
import img5 from "./assets/image5.jpg";

const images = [img1, img2, img3, img4, img5];

const MultiImageSlider = () => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const slideInterval = useRef(null);

  // Items visible based on screen width (breakpoints handled in CSS)
  const getNextIndex = () => {
    return (currentIndex + 1) % images.length;
  };

  useEffect(() => {
    slideInterval.current = setInterval(() => {
      setCurrentIndex((prev) => (prev + 1) % images.length);
    }, 3500); // slide every 3.5 seconds
    return () => clearInterval(slideInterval.current);
  }, []);

  // Number of items shown: 3 on desktop, 2 on tablet, 1 on phone, handled by CSS flex basis

  return (
    <div className="multi-slider-container">
      <div
        className="multi-slider-wrapper"
        style={{
          transform: `translateX(-${currentIndex * 100}%)`,
          width: `${images.length * 100}%`,
        }}
      >
        {images.map((img, index) => (
          <div className="multi-slider-item" key={index}>
            <img src={img} alt={`slide ${index + 1}`} />
          </div>
        ))}
      </div>
    </div>
  );
};

export default MultiImageSlider;
